queries.query:
declare @id bigint; declare @tp varchar(10);
select top 1 @id = folder_id from fs_folders where parent_id {@field='cmp_p'} {@field='parent_id'} and folder_name = {@txtqry='folder_name'};
if(@id is null)
begin
  insert into fs_folders (synch_folder_id, parent_id, folder_name, readed)
    values ({@field='synch_folder_id'}, {@field='parent_id'}, {@txtqry='folder_name'}, 1);
  select @id = @@IDENTITY, @tp = 'insert';  
end
else
begin
 update fs_folders set readed = 1 where folder_id = @id;
 select @tp = 'update';
end
select @id, @tp;

declare @id bigint; declare @tp varchar(10);
select top 1 @id = file_id from fs_files where folder_id = {@field='folder_id'} and file_name = {@txtqry='file_name'};
if(@id is null)
begin
  insert into fs_files (folder_id, file_name, readed)
    values ({@field='folder_id'}, {@txtqry='file_name'}, 1);
  select @id = @@IDENTITY, @tp = 'insert';  
end
else 
begin
 update fs_files set readed = 1 where file_id = @id;
 select @tp = 'update';
end
select @id, @tp;
