CREATE FUNCTION [dbo].[getListOfTagsOfAnagraficaTbl](@idanagrafica integer) RETURNS @ret TABLE ( idtag int ) AS begin declare @idtags as varchar(max); select @idtags = idtags from anagrafiche where idanagrafica = @idanagrafica;
insert into @ret (idtag) SELECT item as idtag FROM dbo.split(replace(@idtags, '[', ''), ']') lst
return end

CREATE FUNCTION [dbo].[getListOfTagsOfSitoTbl](@idsitoweb integer) RETURNS @ret TABLE ( idtag int ) AS begin declare @idtags as varchar(max); select @idtags = idtags from sitiweb where idsitoweb = @idsitoweb;
insert into @ret (idtag) SELECT item as idtag FROM dbo.split(replace(@idtags, '[', ''), ']') lst
return end

CREATE FUNCTION [dbo].[getUtenteChilds](@idUtente int, @id bit) RETURNS varchar(max) AS BEGIN
declare @ids varchar(max);
if((select t.tipoutente from utenti u join tipiutente t on t.IDTipoUtente = u.idtipoutente where u.idutente = @idUtente) = 'user') select @ids = coalesce(@ids + ', ', '') + (case when @id = 1 then cast(tbl.idutente as varchar) else tbl.nome end) from (select u.idutente, u.nome from utenti u join dbo.getUtenteVisibilityIds(@idUtente, 0) vids on u.idutente = vids.idutente union select u.idutente, u.nome from utenti u join tipiutente t on u.idtipoutente = t.idtipoutente where (t.tipoutente = 'anonimo' or u.idutente = @idUtente) and isnull(u.conn, '') = isnull((select conn from utenti where idutente = @idUtente), '')) tbl else if((select t.tipoutente from utenti u join tipiutente t on t.IDTipoUtente = u.idtipoutente where u.idutente = @idUtente) = 'admin') select @ids = coalesce(@ids + ', ', '') + (case when @id = 1 then cast(tbl.idutente as varchar) else tbl.nome end) from (select u.idutente, u.nome from utenti u join tipiutente t on t.IDTipoUtente = u.IDTipoUtente where isnull(u.conn, '') = isnull((select conn from utenti where idutente = @idUtente), '') and u.IDUtente <> @idUtente and t.TipoUtente in ('user', 'admin') union select u.idutente, u.nome from utenti u join tipiutente t on u.idtipoutente = t.idtipoutente where (t.tipoutente = 'anonimo' or u.idutente = @idUtente) and isnull(u.conn, '') = isnull((select conn from utenti where idutente = @idUtente), '')) tbl
RETURN @ids;
END

CREATE FUNCTION [dbo].[getUtenteVisibility](@idUtente int, @diretta bit) RETURNS varchar(max) AS
BEGIN
declare @nomi varchar(max);
--if(@diretta = 1) -- SELECT @nomi = coalesce(@nomi + ', ', '') + uv.nome -- FROM utenti u -- left join utentivisibility v on u.idutente = v.idutente -- left join utenti uv on uv.idutente = v.idutenteview -- where u.idutente = @idUtente --else WITH childs (idutente, nome, livello) AS ( SELECT uv.idutente, uv.nome, 0 as livello FROM utenti u left join utentivisibility v on u.idutente = v.idutente left join utenti uv on uv.idutente = v.idutenteview where u.idutente = @idUtente UNION ALL SELECT uv.idutenteview, u.nome, livello - 1 FROM UtentiVisibility uv inner join childs c on uv.idutente = c.idutente inner join utenti u on u.idutente = uv.idutenteview where @diretta = 0) select @nomi = coalesce(@nomi + ', ', '') + nome from childs order by livello desc
RETURN @nomi;
END

CREATE FUNCTION [dbo].[split](@String varchar(8000), @Delimiter char(1) = ',') returns @temptable TABLE (item varchar(8000)) as begin
declare @slice varchar(8000)
declare @idx int select @idx = 1;
if len(@String) < 1 or @String is null return;
while @idx!= 0 begin set @idx = charindex(@Delimiter,@String) if @idx!=0 set @slice = left(@String,@idx - 1) else set @slice = @String
if(len(@slice)>0) insert into @temptable(Item) values(@slice)
set @String = right(@String,len(@String) - @idx) if len(@String) = 0 break end
return
end
