queries.query:
 declare @parent_id int;
 if ({@txtqry='to'} <> '')
  select @parent_id = id_node from nodes
   where path_node = {@txtqry='to'};

if ({@txtqry='to'} <> '' and @parent_id is null)
begin
  RAISERROR ('hai specificato un path errato!', 15, 10);
  return;
end

declare @node_id int;

insert into nodes (parent_id, title, notes, dt_ins)
 select t2.*
  from (select @parent_id as parent_id, {@txtqry='title'} as title, {@txtqry='notes'} as notes, getdate() as dt_ins
   from (select 1 as col) t) t2;

select @node_id = SCOPE_IDENTITY();
update path_node = dbo.node_path_id(id_node) where id_node = @node_id;
